mapper.select:
SELECT v.*, (CASE WHEN EXISTS (SELECT * FROM tb_audit t WHERE t.`vac.id`=v.id AND t.state=-1) THEN -1 WHEN EXISTS (SELECT * FROM tb_audit t WHERE t.`vac.id`=v.id AND t.state=0) THEN 0 ELSE 1 END) 'state', ${checkerId} 'checkerId' FROM tb_vac v JOIN tb_audit a ON v.id=a.`vac.id` WHERE a.`checker.id`=#{checkerId}
AND (CASE WHEN EXISTS (SELECT * FROM tb_audit t WHERE t.`vac.id`=v.id AND t.state=-1) THEN -1 WHEN EXISTS (SELECT * FROM tb_audit t WHERE t.`vac.id`=v.id AND t.state=0) THEN 0 ELSE 1 END)=#{state}
ORDER BY a.update_time;

SELECT v.id, p.uri, s.name 'sName', s.id 'sId', s.photo FROM tb_vac v,tb_vac_photo p,tb_stu s WHERE s.`grade.id`=#{gradeId} AND v.`sponsor.id`=s.id AND v.`sponsor.type`=0 AND ((v.time_type=2 AND EXISTS (SELECT * FROM tb_vac_date d WHERE d.`vac.id`=v.id AND d.date>=DATE(#{from}) AND d.date<=DATE(#{to}) )
AND TIME(v.start)<=TIME(#{from}) AND TIME(v.end)>=TIME(#{to})
) OR ( v.time_type=3 AND ((v.start<=#{from} AND v.end>=#{from}) OR (v.start>=#{from} AND v.end>=#{to} AND v.start<=#{to}) OR (v.start>=#{from} AND v.end<=#{to}) ))) AND p.`vac.id`=v.id AND p.active=0
